if(windowByName != null){
	windowByName.SendAction(2,3,15,4,0);//最后是副本选择0-5
	ushort elementCount = Core.Memory.Read<ushort>(windowByName.Pointer + 458);
	IntPtr addr = Core.Memory.Read<IntPtr>(windowByName.Pointer + 352);
	var elements = Core.Memory.ReadArray<TwoInt>(addr, elementCount);
	var dps1 = Core.Memory.ReadString((IntPtr) elements[53].Data, Encoding.UTF8);
	var dps2 = Core.Memory.ReadString((IntPtr) elements[54].Data, Encoding.UTF8);
	await Buddy.Coroutines.Coroutine.Sleep(100);

	if( dps1 != "光之巫女" && dps2 != "光之巫女" ){

		switch(dps2){
			case "赤魔法师":
				windowByName.SendAction(2, 3, 12, 4, (ulong) 1); //卸下阿莉塞
				windowByName.SendAction(2, 3, 12, 4, (ulong) 5); //装上琳
			break;
			case "魔女":
				windowByName.SendAction(2, 3, 12, 4, (ulong) 4); //卸下雅修特拉
				windowByName.SendAction(2, 3, 12, 4, (ulong) 5); //装上琳
			break;
		}

		//带阿莉塞
		if(dps1 != "占星术士") { // T N 模式 
			if(dps1 != "赤魔法师") {
				windowByName.SendAction(2, 3, 12, 4, (ulong) 4); //卸下雅修特拉
				windowByName.SendAction(2, 3, 12, 4, (ulong) 1); //装上阿莉塞
			}
		}

	}

	await Buddy.Coroutines.Coroutine.Sleep(100);
	RaptureAtkUnitManager.GetWindowByName("Dawn").SendAction(1,3,14);
}


